import {Component} from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router'
import Patienthtml from './patient.html'
import store from '../../../store/store'

import socketIOClient from "socket.io-client";

var socket;
class Patient extends Component {
    constructor(props){
        super(props)
        this.state = {
            endpoint:'http://localhost:3002',
            Info : {}
        }
    }

    scheduleAppointment = () =>{
        let params = getParams(this.props.authenticate.info.id);
        if (!params || !params.id) {
            return;
        }
        this.id = params.id;
        socket = socketIOClient(this.state.endpoint,{query: `patient=${params.id}`});

    }


    disconnect = (val) =>{
        socket.emit('disconnect-patient', val.id)
        console.log(val);
    }

    connect = (val) =>{
        socket.emit('connect-to-doctor', val.id)
        console.log(val);
    }


    render(){
        return Patienthtml.call(this);
    }


}


const matchStateToProps = state => ({
    authenticate : state.authenticate
})

export default withRouter(connect(matchStateToProps)(Patient))
