import {Component} from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router'
import Operatorhtml from './operator.html'
import store from '../../../store/store'

import socketIOClient from "socket.io-client";
import { getDoc } from '../../../actions/doctors';




var socket;
class Operator extends Component {
    constructor(props){
        super(props)
        this.state = {
            endpoint:'http://localhost:3002',
            status : 'Offline',
            doctorsList :[]
        }

    }

    toggleDoc = (index,key)=>{
        let list = this.state.doctorsList;
        list[index].status = !list[index].status;
        this.setState({doctorsList:list});

    }

    toggle = (e,key)=>{
        if(key.toLowerCase() === 'online'){
            this.setState({status:'Online'});
        }else{
            this.setState({status:'Offline'});
        }
    }

    processDoc = (docList) =>{
        this.setState({doctorsList:docList.data});
    }

    disconnect = (val) =>{
        socket.emit('disconnect-patient', val.id)
        console.log(val);
    }

    connect = (val) =>{
        socket.emit('connect-to-doctor', val.id)
        console.log(val);
    }

    renderData= ()=>{
        console.log(this.state.data);
        return null;
    }

    componentDidMount(){
        store.dispatch(getDoc({fn:this.processDoc,scope:this}))
        socket = socketIOClient(this.state.endpoint,{query: `operator=${this.props.authenticate?this.props.authenticate.info.name:'test'}`});
        socket.on('patients',patients => {this.setState({data:patients})});

    }

    render(){
        return Operatorhtml.call(this);
    }


}


const matchStateToProps = state => ({
    authenticate : state.authenticate
})

export default withRouter(connect(matchStateToProps)(Operator))
