        super(props)
        this.state = {
            endpoint:'http://localhost:3002',
            Info : {}
        }
    }

    getParams = (query) =>{
        if (!query) {
            return { };
        }

        return (/^[?#]/.test(query) ? query.slice(1) : query)
        .split('&')
        .reduce((params, param) => {
            let [ key, value ] = param.split('=');
            params[key] = value ? decodeURIComponent(value.replace(/\+/g, ' ')) : '';
            return params;
        }, { });
    }

    scheduleAppointment = () =>{
        let params = this.getParams('?id=100');

        if (!params || !params.id) {
            return;
        }
        this.id = params.id;
        try{
            socket = socketIOClient(this.state.endpoint,{query: `patient=${params.id}`});
            toastr.success("appointment scheduled")
        }catch(err){
            toastr.error("appointment scheduled failed")
        }

        to
        socket.on('update', data => {
            this.info = data;
        });
        socket.on('connect', _ => {
            console.log(`Connected.`);
        });

        socket.on('disconnect', function () {
            console.log('you have been disconnected');
        });

        socket.on('reconnect', function () {
            console.log('you have been reconnected');
        });

        socket.on('reconnect_error', function () {
            console.log('attempt to reconnect has failed');
        });

    }

    render(){
        return Patienthtml.call(this);
    }


}


const matchStateToProps = state => ({
    authenticate : state.authenticate
})

export default withRouter(connect(matchStateToProps)(Patient))
